// Time in seconds
void A_Splash(char time)
{
	imgdat_t id;
	id.data = splashdata;
	id.frames = 2;
	id.height = SPLASHH;
	id.width = SPLASHW;

	extern int wwidth, wheight;
	sprite_t *s = D_NewSprite(wwidth/2-SPLASHW/2, wheight/2-SPLASHH/2, &id, SPRITELAYERS-1);

	unsigned savefps = (unsigned)A_VarValue("maxfps");
	A_SetVar("maxfps", "5");

	int framecount = 0;
	while (framecount/5.0f <= time)
	{
		s->frame=!s->frame;
		D_Draw();
		framecount++;
	}

	// Resetting the maxfps
	char buf[24] = {0};
	itoa(savefps, buf, 10);
	A_SetVar("maxfps", buf);

	D_DeleteSprite(s);
}

#endif

int A_FindArg(char *arg)
{
	for (int i = 0; i < ave_c; i++)
	{
		if (!strcmp(arg, ave_v[i]))
			return i;
	}
	return -1;
}

char relpath[256];
char *A_RelPath(const char *fp)
{
	puts(exepath);
	strcpy(relpath, exepath);
	strcat(relpath, fp);
	return relpath;
}

